import { useState } from 'react'
import Circles from '../../assets/Circles.png'

export default function StyleHandler ( { sequenceStyle, setSequenceStyle } ) {

    const [styleColor, setSyleColor] = useState()
    const [isStyle, setIsStyle] = useState(false)



    function setStyling() {
        setSequenceStyle(!sequenceStyle)

    }

    return (
        <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100%', paddingLeft: 6, paddingRight: 6 }}>
            <img onClick={setStyling} src={Circles} style={{ height: '80%', cursor: 'pointer', backgroundColor: styleColor }} alt="style icon: set styling options" />
        </span>
    )
}
