export default function SeqRange () {

    const radius = 30
    const height = 25

    function handleFocus(event) {
        event.target.style.border = 'none'
        event.target.style.outline = 'none'
    }

    function handleSubmit() {
        console.log(document.getElementById('range-begin').value)
        document.getElementById('range-end').value = ''
    }

    return (
        <form autoComplete='off' onSubmit={handleSubmit}>   
        <div style={{height: height, width: 'fit-content', backgroundColor: 'white', borderRadius: radius, marginLeft: 20, display: 'flex', flexDirection: 'row', position: 'relative', zIndex: 3 }}>
                 
                <input id='range-begin' type='text' placeholder='1' style={{ height: height-2, width: 60, backgroundColor: 'white', borderRadius: radius, border: 'none' }} onFocus={(event) => handleFocus(event)}></input>
                <input id='range-end' type='text' placeholder='1000' style={{ height: height-2, width: 60, backgroundColor: 'white', borderRadius: radius, border: 'none' }} onFocus={(event) => handleFocus(event)}></input>
            
        </div>
        </form>
    )
}
